import { IPC_DEFAULT_API_ROOT } from "@electron-ipc-controller/shared";

export const generateGlobalTypes = (apiRoot: string = IPC_DEFAULT_API_ROOT, ipcApiImportPath: string): string => {
  const isValidIdentifier = /^[A-Za-z_$][A-Za-z0-9_$]*$/.test(apiRoot);
  const windowProperty = isValidIdentifier ? `${apiRoot}: IpcApi;` : `[${JSON.stringify(apiRoot)}]: IpcApi;`;

  return `// Auto-generated by @electron-ipc-controller/vite-plugin
// Do not edit manually

import type { IpcApi } from "${ipcApiImportPath}";

declare global {
  interface Window {
    ${windowProperty}
  }
}

export {};
`;
};
